String.prototype.removeNbsp=function(){return this.replace(/[\u00A0+]/g," ");};$('.wiki-content').find('.confluenceTable').each(function(){$(this).find('tr').each(function(){var labelEl=$(this).children().eq(0),valueEl=$(this).children().eq(1),label=labelEl.text().toLowerCase().removeNbsp().trim();if(label=='video id'){var videoId=valueEl.text().removeNbsp().trim().replace('http://www.youtube.com/watch?v=','').replace('v=','').replace('http://youtu.be/','');if(videoId){$(this).addClass('yt-video').attr('videoId',videoId);var vidFrame=$('<iframe width="560" height="315" src="" frameborder="0" allowfullscreen></iframe>').attr('src','//www.youtube.com/embed/'+videoId);insertRow($(this),'Preview',vidFrame);}}});});function insertRow(rowEl,labelStr,valueEl){var newRow=$('<tr></tr>'),rowHead=$('<th>**</th>').addClass('confluenceTh'),blankTd=$('<td></td>').addClass('confluenceTd').attr('colspan','3');rowHead.text(labelStr).css({'background-color':'#c1d2de'}).appendTo(newRow);valueEl.appendTo(blankTd.appendTo(newRow));newRow.insertBefore(rowEl);}